import { TComputer, TPlayBody } from "../services/types/types";

const ApiUrlPath2 = 'http://localhost:8003';
const ApiUrlPath = 'http://172.20.10.4';

const request = (endpoint: string, options: any) => {
    const url = `${ApiUrlPath}${endpoint}`;
    return fetch(url, options).then(checkResponse);
};

const checkResponse = (res: Response) => {
    return res.ok
        ? res.json()
        : res.json().then((err) => Promise.reject(err));
};

// fetchStoreData
export const fetchStoreData = async () => {
    const route = "/store";
    const endpoint = "/items";
    const options = {
        headers: {
            auth: 123
        },
    }
    return request(endpoint, options);
}

export const fetchComputersData = async () => {
    const endpoint = "/pc" + "/ping";
    const options = {
        headers: {
            auth: 123
        },
    }
    return request(endpoint, options);
}
export const fetchPlay = async (body: TPlayBody) => {
    const endpoint = "/pc" + "/play";
    const options = {
        method: "PATCH",
        headers: {
            'Content-Type': 'application/json',
            auth: 123
        },
        body: JSON.stringify(body)
    }
    return request(endpoint, options);
}
export const fetchPause = async (id: number) => {
    const endpoint = "/pc" + `/pause?id=${id}`;
    const options = {
        method: "PATCH",
        headers: {
            'Content-Type': 'application/json',
            auth: 123
        },
    }
    return request(endpoint, options);
}
export const fetchContinue = async (id: number) => {
    const endpoint = "/pc" + `/continue?id=${id}`;
    const options = {
        method: "PATCH",
        headers: {
            'Content-Type': 'application/json',
            auth: 123
        },
    }
    return request(endpoint, options);
}
export const fetchFinish = async (computer: TComputer, newPrice: number | undefined) => {
    const endpoint = "/pc" + "/finish";
    const options = {
        method: "PATCH",
        headers: {
            'Content-Type': 'application/json',
            auth: 123
        },
        body: newPrice ? JSON.stringify({
            id: computer.id,
            price: (newPrice)
        }) : JSON.stringify({
            id: computer.id,
        })
    }
    return request(endpoint, options);
}
