import React, { FC } from 'react';
import styles from './ConstructorItem.module.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlayCircle } from '@fortawesome/free-solid-svg-icons';

type Props = {
    name: string,
    index: number
};

const ConstructorItem: FC<Props> = ({ name, index }) => {
    const [isEditing, setIsEditing] = useState(false);
    const [hours, setHours] = useState('');
    const [minutes, setMinutes] = useState('');
    const [money, setMoney] = useState('');

    const handlePlayClick = () => {
        setIsEditing(true);
    }

    const handleAcceptClick = () => {
        // Ваша логика для обработки введенных данных
        // Например, отправка данных на сервер или вызов других функций
        setIsEditing(false);
    }

    if (isEditing) {
        return (
            <article className={styles.article}>
                <div className={styles.item}>
                    <h2>PC - {index}</h2>
                    <label>
                        Hours:
                        <input type="text" value={hours} onChange={(event) => setHours(event.target.value)} />
                    </label>
                    <label>
                        Minutes:
                        <input type="text" value={minutes} onChange={(event) => setMinutes(event.target.value)} />
                    </label>
                    <label>
                        Money:
                        <input type="text" value={money} onChange={(event) => setMoney(event.target.value)} />
                    </label>
                    <button className={styles.button} onClick={handleAcceptClick}>
                        Accept
                    </button>
                </div>
            </article>
        );
    }

    return (
        <article className={styles.article}>
            <div className={styles.item}>
                <h2>PC - {index}</h2>
                <button className={styles.button} onClick={handlePlayClick}>
                    <FontAwesomeIcon icon={faPlayCircle} />
                    Play
                </button>
            </div>
        </article>
    );


    // return (
    //     <article className={styles.article}>
    //         <div className={styles.item}>
    //             <h2>PC - {index}</h2>
    //             <button className={styles.button} onClick={handlePlayClick}>
    //                 <FontAwesomeIcon icon={faPlayCircle} />
    //                 Play
    //             </button>
    //         </div>
    //     </article>
    // );
}

export default ConstructorItem;
