import React, { FC, useState, useEffect } from 'react';
import styles from './ConstructorItem.module.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlayCircle } from '@fortawesome/free-solid-svg-icons';

type Props = {
    name: string,
    index: number
};

const ConstructorItem: FC<Props> = ({ name, index }) => {
    const [isEditing, setIsEditing] = useState(false);
    const [hours, setHours] = useState('');
    const [minutes, setMinutes] = useState('');
    const [money, setMoney] = useState('');
    const [isBooked, setIsBooked] = useState(false);
    const [bookedUntil, setBookedUntil] = useState('');
    const [paymentAmount, setPaymentAmount] = useState('');

    const handlePlayClick = () => {
        setIsEditing(true);
    }

    const handleAcceptClick = () => {
        // Ваша логика для обработки введенных данных
        // Например, отправка данных на сервер или вызов других функций
        setIsEditing(false);
        setIsBooked(true);

        const currentTime = new Date();
        const bookedTime = new Date(currentTime.getTime() + (parseInt(hours) * 60 + parseInt(minutes)) * 60000);
        const formattedBookedTime = bookedTime.toLocaleTimeString();
        setBookedUntil(formattedBookedTime);
        setPaymentAmount(money);

        setTimeout(() => {
            setIsBooked(false);
        }, bookedTime.getTime() - currentTime.getTime());
    }

    useEffect(() => {
        if (isBooked) {
            const currentTime = new Date();
            const bookedTime = new Date(bookedUntil);
            if (bookedTime.getTime() <= currentTime.getTime()) {
                setIsBooked(false);
            }
        }
    }, [bookedUntil, isBooked]);

    if (isEditing) {
        return (
            <article className={styles.article}>
                <div className={`${styles.item} ${styles.mb4}`}>
                    <h2>PC - {index}</h2>
                    <label>
                        Hours:
                        <input type="text" value={hours} onChange={(event) => setHours(event.target.value)} />
                    </label>
                    <label>
                        Minutes:
                        <input type="text" value={minutes} onChange={(event) => setMinutes(event.target.value)} />
                    </label>
                    <label>
                        Money:
                        <input type="text" value={money} onChange={(event) => setMoney(event.target.value)} />
                    </label>
                    <button className={styles.button} onClick={handleAcceptClick}>
                        Accept
                    </button>
                </div>
            </article>
        );
    }

    if (isBooked) {
        return (
            <article className={styles.article}>
                <div className={styles.item}>
                    <h2>PC - {index}</h2>
                    <p>Занято</p>
                    <p>Booked until: {bookedUntil}</p>
                    <p>Payment amount: {paymentAmount}</p>
                </div>
            </article>
        );
    }

    return (
        <article className={styles.article}>
            <div className={styles.item}>
                <h2>PC - {index}</h2>
                <button className={styles.button} onClick={handlePlayClick}>
                    <FontAwesomeIcon icon={faPlayCircle} />
                    Play
                </button>
            </div>
        </article>
    );
}

export default ConstructorItem;
