import React, { FC, useState } from 'react';
import styles from "./StorePage.module.css";
import data from "../../utils/store.json";

export const StorePage: FC = () => {
    const [selectedItems, setSelectedItems] = useState<any[]>([]);

    const handleItemClick = (itemId: any) => {
        if (selectedItems.includes(itemId)) {
            setSelectedItems(selectedItems.filter((id) => id !== itemId));
        } else {
            setSelectedItems([...selectedItems, itemId]);
        }
    };

    const handleAddToCart = () => {
        const selectedProducts = data.filter((item) => selectedItems.includes(item._id));
        console.log("Выбранные товары:", selectedProducts);
    };

    return (
        <main className={styles.container}>
            <h1>Магазин</h1>
            <p>Какие-то данные с базы данных</p>

            <div className={styles.card}>
                <div>
                    <table className={styles.table}>
                        <thead>
                            <tr>
                                <th>Название</th>
                                <th>Стоимость</th>
                                <th>Количество</th>
                                <th>Действия</th>
                            </tr>
                        </thead>
                        <tbody>
                            {data.map((item) => (
                                <tr
                                    key={item._id}
                                    className={selectedItems.includes(item._id) ? styles.selectedRow : ""}
                                    onClick={() => handleItemClick(item._id)}
                                >
                                    <td>{item.name}</td>
                                    <td>{item.price}</td>
                                    <td>{item.psc}</td>
                                    <td>
                                        <button onClick={handleAddToCart}>Добавить</button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>
        </main>
    )
};
