        headers: {
            auth: 123
        },
    }
    return request(endpoint, options);
}
export const fetchContinue = async () => {
    const endpoint = "/continue";
    const options = {
        method: "patch",
        headers: {
            auth: 123
        },
    }
    return request(endpoint, options);
}
export const fetchFinish = async () => {
    const endpoint = "/finish";
    const options = {
        method: "patch",
        headers: {
            auth: 123
        },
    }
    return request(endpoint, options);
}
